}

const comparator = (nodeA, nodeB) => {
    let a = nodeA.dis;
    let b = nodeB.dis;
    return (a > b ? 1 : a < b ? -1 : 0);    
}

export function dijkstra(grid, startNode, finishNode) {
    const visitedNodesInOrder = [];
    startNode.distance = 0;

    const pq = new PriorityQueue({comparator});
    pq.push(new Pair(0, startNode));
    while (!pq.isEmpty()) {

      const from = pq.top().cell;
      const pq_dis = pq.top().dis;
      visitedNodesInOrder.push(from);
      pq.pop();

      if(pq_dis > from.distance) continue;
      if (from.isWall) continue;
      if (from === finishNode) return visitedNodesInOrder;
      const neighbors = getNeighbors(from, grid);

      for (const to of neighbors) {
          if( pq_dis + 1  < to.distance ){
              to.distance = from.distance + 1;
              to.previousNode = from;
              pq.push(new Pair(to.distance, to));
            //   if(from == startNode)console.log(pq);
            }
      }
    }
    return visitedNodesInOrder;
  }

  function getNeighbors(node, grid) {
    const neighbors = [];
    const {col, row} = node;
    if (row > 0) neighbors.push(grid[row - 1][col]);
    if (row < grid.length - 1) neighbors.push(grid[row + 1][col]);
    if (col > 0) neighbors.push(grid[row][col - 1]);
    if (col < grid[0].length - 1) neighbors.push(grid[row][col + 1]);
    return neighbors;
  }

  // Backtracks from the finishNode to find the shortest path.
  // Only works when called *after* the dijkstra method above.
  export function getNodesInShortestPathOrder(finishNode) {
    const nodesInShortestPathOrder = [];
    let currentNode = finishNode;
    while (currentNode !== null) {
      nodesInShortestPathOrder.unshift(currentNode);
      currentNode = currentNode.previousNode;
    }
    return nodesInShortestPathOrder;
  }
