import React, { Component } from "react";
import Cell from "./Cell/Cell";
import { Button, Navbar } from "react-bootstrap";
import { dijkstra, getShortestPath } from "./Algorithms/dikjstra";
import { depthFirstSearch, getPath } from "./Algorithms/dfs";
import "./simulator.css";

const START_NODE_ROW = 12;
const START_NODE_COL = 1;
const FINISH_NODE_ROW = 12;
const FINISH_NODE_COL = 52;

export default class RouteFinder extends Component {
    constructor() {
        super();
        this.state = {
            grid: [],
            mouseIsPressed: false,
    };
}

componentDidMount() {
    const grid = getInitialGrid();
    this.setState({ grid });
}

handleMouseDown(row, col) {
    const newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
    this.setState({ grid: newGrid, mouseIsPressed: true });
}

handleMouseEnter(row, col) {
    if (!this.state.mouseIsPressed) return;
    const newGrid = getNewGridWithWallToggled(this.state.grid, row, col);
    this.setState({ grid: newGrid });
}

handleMouseUp() {
    this.setState({ mouseIsPressed: false });
}

runSimulation(allPath, reqPath) {
    for (let i = 0; i <= allPath.length ; i++) {

        if (i === allPath.length) {
            setTimeout(() => {
                this.runShortestPath(reqPath);
            }, 10 * i);
            return;
        }

        const node = allPath[i];
        if(START_NODE_ROW===node.row && START_NODE_COL===node.col) continue;
        let nodeState = `node node-visited`;
        if(node.isFinish)
            nodeState = `node node-finish node-visited`

        console.log(`animating`);
        setTimeout(() => {
            document.getElementById(`node-${node.row}-${node.col}`).className = nodeState;
        }, 10 * i);
    }
}

runShortestPath(reqPath) {
    for (let i = 0; i < reqPath.length ; i++) {
        const node = reqPath[i];
        if(START_NODE_ROW===node.row && START_NODE_COL===node.col) continue;
        setTimeout(() => {
            let nodeState = `node node-shortest-path`;
            if(node.isFinish)
                nodeState = `node node-finish node-shortest-path`
            document.getElementById(`node-${node.row}-${node.col}`).className = nodeState;
        }, 50 * i);
    }
}

performDijkstra() {
    const { grid } = this.state;
    const start = grid[START_NODE_ROW][START_NODE_COL];
    const end = grid[FINISH_NODE_ROW][FINISH_NODE_COL];
    const allPath = dijkstra(grid, start, end);
    const reqPath = getShortestPath(end);
    this.runSimulation(allPath, reqPath)
}

performDFS(){
    const { grid } = this.state;
    const start = grid[START_NODE_ROW][START_NODE_COL];
    const end = grid[FINISH_NODE_ROW][FINISH_NODE_COL];
    const allPath = depthFirstSearch(start, end, grid);
    const reqPath = getPath(end);
    this.runSimulation(allPath, reqPath);
}
clearBoard() {
    const { grid } = this.state;
    const newGrid = grid.slice();
    for (let row = 0; row < 24; row++) {
      for (let col = 0; col < 54; col++) {
        const node = newGrid[row][col];
        const newNode = {
          ...node,
          isWall: false,
          isStart: START_NODE_ROW===row && START_NODE_COL===col ? true : false,
          isFinish: FINISH_NODE_ROW===row && FINISH_NODE_COL===col ? true : false
        };
        newGrid[row][col] = newNode;
      }
    }
    this.setState({ grid: newGrid });
}

fullWall() {
    for (let row = 0; row < 24; row++) {
      for (let col = 0; col < 54; col++) {
        if(START_NODE_ROW===row && START_NODE_COL===col) continue;
        if(FINISH_NODE_ROW===row && FINISH_NODE_COL===col) continue;
        const newGrid = getNewGridWithWallToggled(this.state.grid, row, col, true);
        this.setState({ grid: newGrid });
      }
    }
}

render() {
    const { grid, mouseIsPressed } = this.state;

    return (
      <>
        <Navbar bg="dark" variant="dark">
          <Navbar.Brand href="root">Route Finder</Navbar.Brand>
          <Button
            className="topRow"
            variant="light"
            onClick={() => this.performDijkstra()}
          >
            Dijkstra's Algorithm
          </Button>{" "}
          <Button
            className="topRow"
            variant="light"
            onClick={() => this.performDFS()}
          >
            Depth First Search
          </Button>{" "}
          <Button
            className="topRow"
            variant="secondary"
            onClick={() => this.clearBoard()}
          >
            Draw Maze
          </Button>{" "}
          <Button
            className="topRow"
            variant="secondary"
            onClick={() => this.fullWall()}
          >
            Draw Path
          </Button>
          <Button className="sidebar" href="root" variant="warning">
            Reset
          </Button>
        </Navbar>

        <div className="col">
          {grid.map((row, rowIdx) => {
            return (
              <div className = "row" key={rowIdx}>
                {row.map((node, nodeIdx) => {
                  const { row, col, isFinish, isStart, isWall } = node;

                  return (
                    <Cell
                      key={nodeIdx}
                      col={col}
                      isFinish={isFinish}
                      isStart={isStart}
                      isWall={isWall}
                      mouseIsPressed={mouseIsPressed}
                      onMouseDown={(row, col) => this.handleMouseDown(row, col)}
                      onMouseEnter={(row, col) =>
                        this.handleMouseEnter(row, col)
                      }
                      onMouseUp={() => this.handleMouseUp()}
                      row={row}
                    ></Cell>

                  );

                })}
              </div>
            );
          })}
        </div>
      </>
    );
  }
}

const getInitialGrid = () => {
    const grid = [];
    for (let row = 0; row < 24; row++) {
        const currentRow = [];
        for (let col = 0; col < 54; col++) {
        currentRow.push(createNode(col, row));
        }
        grid.push(currentRow);
    }
    return grid;
};

const createNode = (col, row) => {
    return {
        col,
        row,
        isStart: row === START_NODE_ROW && col === START_NODE_COL,
        isFinish: row === FINISH_NODE_ROW && col === FINISH_NODE_COL,
        distance: Infinity,
        isVisited: false,
        isWall: false,
        previousNode: null,
    };
};

const getNewGridWithWallToggled = (grid, row, col, allWall = false) => {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    const newNode = {
        ...node,
        isWall: allWall ? true : !node.isWall
    };
    newGrid[row][col] = newNode;
    return newGrid;
};
